    pInfoSize = Ref{UInt}()
    @repeat_while_incomplete begin
            @check vkGetShaderInfoAMD(device, pipeline, VkShaderStageFlagBits(shader_stage.val), info_type, pInfoSize, C_NULL, fun_ptr)
