function Base.getproperty(x::ClearColorValue, sym::Symbol)
    if sym === :float32
        x.data.float32
    elseif sym === :int32
        x.data.int32
    elseif sym === :uint32
        x.data.uint32
    else
        getfield(x, sym)
    end
end

function Base.getproperty(x::ClearValue, sym::Symbol)
    if sym === :color
        x.data.color
    elseif sym === :depth_stencil
        x.data.depthStencil
    else
        getfield(x, sym)
    end
end

function Base.getproperty(x::PerformanceCounterResultKHR, sym::Symbol)
    if sym === :int32
        x.data.int32
    elseif sym === :int64
        x.data.int64
    elseif sym === :uint32
        x.data.uint32
    elseif sym === :uint64
        x.data.uint64
    elseif sym === :float32
        x.data.float32
    elseif sym === :float64
        x.data.float64
    else
        getfield(x, sym)
    end
end

function Base.getproperty(x::PerformanceValueDataINTEL, sym::Symbol)
    if sym === :value32
        x.data.value32
    elseif sym === :value64
        x.data.value64
    elseif sym === :value_float
        x.data.valueFloat
    elseif sym === :value_bool
        x.data.valueBool
    elseif sym === :value_string
        x.data.valueString
    else
        getfield(x, sym)
    end
end

function Base.getproperty(x::PipelineExecutableStatisticValueKHR, sym::Symbol)
    if sym === :b32
